import React from 'react';
import Select from 'react-select';

const Table = ({ breweries }) => {
  return (
      <div>
    <Select options={breweries.city} />
    <table className="table">
      <thead>
        <tr>
          <th>Id</th>
          <th>Name</th>
          <th>Type</th>
          <th>Address</th>
          <th>Website URL</th>
        </tr>
      </thead>
      <tbody>
      { (breweries.length > 0) ? breweries.map( (brewery, index) => {
          let breweryDetail = "https://api.openbrewerydb.org/breweries/"+brewery.id;
           return (
            <tr key={ index }>
              <td>{ brewery.id }</td>
              <td>
                <a href={breweryDetail}>
              { brewery.name }</a>
              </td>
              <td>{ brewery.brewery_type}</td>
              <td>{ brewery.street }, { brewery.city }, { brewery.state } { brewery.postal_code }</td>
              <td><a href={brewery.website_url}>{ brewery.website_url }</a></td>
            </tr>
          )
         }) : <tr><td colSpan="5">Loading...</td></tr> }
      </tbody>
    </table></div>
  );
}

export default Table
