import React from 'react';

export default class FetchBrewery extends React.Component {
    state= {
        loading: true
    };

    async componentDidMount() {
        const url = "https://api.openbrewerydb.org/breweries/";
        const response = await fetch (url);
        const data = await response.json();
        this.setState({brewery: data[0], loading: false });
    }

    render() {
        if (this.state.loading) {
            return <div>loading</div>
        }

        if (!this.state.brewery) {
            return <div>No data</div>
        }
        return (
            <div>
                    <div>
                       <div> {this.state.brewery.name} {this.state.brewery.brewery_type} 
                       {this.state.brewery.street} {this.state.brewery.city} {this.state.brewery.state} {this.state.brewery.postal_code}
                       {this.state.brewery.website_url} 
                       </div>
                    </div>
            </div>
        );
    }
}
