import React from 'react';
import { Map, GoogleApiWrapper } from 'google-maps-react';

// const MapContainer = ({ text }) => <div>{text}</div>;
const mapStyles = {
    width: '100%',
    height: '100%',
  };
export class BreweryMap extends React.Component{
    constructor(props) {
        super(props);
        this.center = {
            lat: this.props.brewery.latitude,
            lng: this.props.brewery.longitude
          }
          this.zoom= 11
    }


    render() {
        return(
            <Map
            google={this.props.google}
            zoom={8}
            style={mapStyles}
            initialCenter={{ lat: 47.444, lng: -122.176}}
            />
        );
    }
}
//export default BreweryMap;

export default GoogleApiWrapper({
    apiKey: 'AIzaSyA-7d1-o3I4_FPxDYGOM6OAmON9zPdaZ-Q '
  })(BreweryMap);
