import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'

class FetchOpenBreweryAPI extends React.Component() {

  constructor(props) {
    super(props);
    this.state= {
      loading: true
    }
  }

async componentDidMount() {
    const url = "https://api.openbrewerydb.org/breweries/";
    const response = await fetch (url);
    const data = await response.json();
    this.setState({brewery: data, loading: false });
}

renderTableData() {
  return this.state.brewery.map((brewery, index) => {
     const { id, name, brewery_type, street, city, state, postal_code, website_url } = brewery; //destructuring
     return (
        <tr key={id}>
           <td>{id}</td>
           <td>{name}</td>
           <td>{brewery_type}</td>
           <td>{street} {city} {state} {postal_code}</td>
           <td>{website_url}</td>
        </tr>
     )

  })
}

renderTableHeader() {
   let header = Object.keys(this.state.brewery[0])
   //const { id, name, brewery_type, website_url } = header ;//destructuring
   return header.map((key, index) => {
       return <th key={index}>{key.toUpperCase()}</th>
   })
}
