        if (this.state.loading) {
            return <div>loading</div>
        }

        if (!this.state.brewery) {
            return <div>No data</div>
        }
        return (
            <div>
                <h1 id="title">Breweries</h1>
                <table id="brewery">
                    <thead>
                        <tr>{this.renderTableHeader()}</tr>
                    </thead>
                    <tbody>
                        {this.renderTableData()}
                    </tbody>
                </table>
            </div>
        );
    }
}

