import React from 'react';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';

const mapStyles = {
    width: '100%',
    height: '80%',
  };
export class BreweryMap extends React.Component{
    constructor(props) {
        super(props);
        this.center = {
            lat: 59.95,
            lng: 30.33
          };
        this.zoom= 11;
        this.breweryLat= this.props.brewery["latitude"],
        this.breweryLng= this.props.brewery["longitude"]
    }



    render() {
        return(
            <Map
            google={this.props.google}
            zoom={8}
            style={mapStyles}
            initialCenter={{ lat: 47.444, lng: -122.176}}
            >
            <Marker position={{ lat: this.brewery.lat, lng:this.brewery.lng }} />
            </Map>
        );
    }
}
//export default BreweryMap;

export default GoogleApiWrapper({
    apiKey: 'AIzaSyA-7d1-o3I4_FPxDYGOM6OAmON9zPdaZ-Q '
  })(BreweryMap);
