import React from 'react';
import './App.css';
import FetchBrewery from './components/FetchBrewery'
import FetchOpenBreweryAPI from './components/FetchOpenBreweryAPI';
import BreweryDroplets from './components/BreweryDroplets';
import Table from './components/Table';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      droplets: []
    }
  }
  componentDidMount() {
    fetch('https://api.openbrewerydb.org/breweries')
    .then(res => res.json())
    .then(json => json.droplets)
    .then(droplets => this.setState({ 'droplets': droplets }))
  }

  render () {
    return (
      <div className="App">
        <Table droplets={ this.state.droplets } />
    </div>
    )
  };
}

export default App;
