import React from 'react';
import './App.css';
import logo from './logo.svg'
//import FetchBrewery from './components/FetchBrewery'
//import FetchOpenBreweryAPI from './components/FetchOpenBreweryAPI';
//import BreweryDroplets from './components/BreweryDroplets';
import Table from './components/Table';
import 'bootstrap/dist/css/bootstrap.min.css';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      droplets: []
    }
  }
  async componentDidMount() {
    const url = "https://api.openbrewerydb.org/breweries/";
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ droplets: data, loading: false });
    }

  render () {
    return (
      <div className="App">
      <nav className="navbar navbar-light bg-light">
                    <a className="navbar-brand" href="./">
                        <img src={logo} alt="logo" width="40" /> Breweries
                    </a>
                </nav>
        <Table droplets={ this.state.droplets } />
    </div>
    )
  };
}

export default App;
