import React from 'react';

class BreweryDetail extends React.Component {
    constructor(props) {
        super(props);
        this.state = {breweryDetails: "", loading: false }
    }

    async componentDidMount() {
        const url = "https://api.openbrewerydb.org/breweries/"+this.props.breweryId;
        const response = await fetch(url);
        const data = await response.json();
        this.setState({ breweryDetails: data, loading: false });
    }

    render(){
        return(
        <table width="100%">
            <tr>
                <td>{this.state.breweryDetails["name"]}</td>
                <td>{this.state.breweryDetails["brewery_type"]}</td>
                <td>
                {this.state.breweryDetails["street"]+ ", " +
                this.state.breweryDetails["city"]+ ", " +
                this.state.breweryDetails["state"]+ " " +
                this.state.breweryDetails["postal_code"]}

                </td>

                <td>{this.state.breweryDetails["name"]}</td>
            </tr>

        </table>
        )
    }
}
export default BreweryDetail;
