import React from 'react';

class FetchBrewery extends React.Component {
    state= {
        loading: true
    };

    async componentDidMount() {
        const url = "https://api.openbrewerydb.org/breweries/";
        const response = await fetch (url);
        const data = await response.json();
        this.setState({brewery: data, loading: false });
    }

    renderTableData() {
      return this.state.brewery.map((brewery, index) => {
         const { id, name, brewery_type, street, city, state, postal_code, website_url } = brewery; //destructuring
         return (
            <tr key={id}>
               <td>{id}</td>
               <td>{name}</td>
               <td>{brewery_type}</td>
               <td>{street} {city} {state} {postal_code}</td>
               <td>{website_url}</td>
            </tr>
         )

      })
   }

   renderTableHeader() {
       let header = Object.keys(this.state.brewery[0])
       //const { id, name, brewery_type, website_url } = header ;//destructuring
       return header.map((key, index) => {
           return <th key={index}>{key.toUpperCase()}</th>
       })
   }
   renderbreweryAddress() {

   }

    render() {
        if (this.state.loading) {
            return <div>loading</div>
        }

        if (!this.state.brewery) {
            return <div>No data</div>
        }
        return (
            <div>
                <h1 id="title">Breweries</h1>
                <table id="brewery">
                    <thead>
                        <tr>{this.renderTableHeader()}</tr>
                    </thead>
                    <tbody>
                        {this.renderTableData()}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default FetchBrewery
