import React from 'react';

export default class FetchBrewery extends React.Component {
    state= {
        loading: true
    };

    async componentDidMount() {
        const url = "https://api.openbrewerydb.org/breweries/";
        const response = await fetch (url);
        const data = await response.json();
        this.setState({brewery: data.results[0] });
    }

    render() {
        return (
            <div>
                {this.state.loading || !this.state.brewery?(
                    <div>loading data...</div>
                     ) : (
                    <div>
                       <div> {this.state.brewery.name} </div>
                    </div>

                ) }
            </div>
        );
    }
}
