import React from 'react';
import './App.scss';
import logo from './logo.svg'
import Brewery from './components/Brewery';
import 'bootstrap/dist/css/bootstrap.min.css';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      breweries: []
    }
    this.counter = 0;
    this.searchByCity = this.searchByCity.bind(this);
  }
  async componentDidMount() {
    const url = "https://api.openbrewerydb.org/breweries/";
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ breweries: data, loading: false });
  }

  async searchByCity(e) {
    let SearchKeyword= e.target.value;
    //if(e.target.value !== "") {
      const url = "https://api.openbrewerydb.org/breweries?by_city="+ SearchKeyword;
      const response = await fetch(url);
      const data = await response.json();
      this.setState({ breweries: data, loading: false });
    //}

  }

  render () {
    let breweryList = this.state.breweries.map(brewery, index =>{
      let breweryProps = {brewery: brewery, counter: ++this.counter };
      return(
        <Brewery brewery={breweryProps} />
      )
    });
    return (
      <div className="App main">
        <nav className="navbar navbar-light bg-light">
        <a className="navbar-brand" href="./"> 
          <img src={logo} alt="logo" width="40" /> Breweries 
        </a>
        </nav>

        <nav className="navbar navbar-light bg-light">
        <input type="text" className="input" placeholder="Search City ..." onChange={this.searchByCity} />
        </nav>

        {<BreweryTable breweries={ this.state.breweries } /> }

    </div>
    )
  };
}



export default App;
