import React from 'react';
import './App.css';
import logo from './logo.svg'
import Table from './components/Table';
import 'bootstrap/dist/css/bootstrap.min.css';
import Select from 'react-select';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      breweries: []
    }
  }
  async componentDidMount() {
    const url = "https://api.openbrewerydb.org/breweries/";
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ breweries: data, loading: false });
    }

  searchByCity(e) {
    let breweries = [];
    let filteredBreweries = [];

    if(e.target.value !== "") {
      breweries = this.props.items;
    }
  }

  render () {

    return (
      <div className="App">
      <nav className="navbar navbar-light bg-light">
          <a className="navbar-brand" href="./">
              <img src={logo} alt="logo" width="40" /> Breweries
          </a>
      </nav>
      <nav className="navbar navbar-light bg-light">
        <input type="text" className="input" placeholder="Search City ..." />
      </nav>
        {<Table breweries={ this.state.breweries } /> }
    </div>
    )
  };
}

export default App;
