import React from 'react';

const Table = ({ droplets }) => {
  return (
    <table className="table">
      <thead>
        <tr>
          <th>Id</th>
          <th>Name</th>
          <th>Type</th>
          <th>Address</th>
          <th>Website URL</th>
        </tr>
      </thead>
      <tbody>
      { (droplets.length > 0) ? droplets.map( (droplet, index) => {
          let breweryDetail = "https://api.openbrewerydb.org/breweries/"+droplet.id;
           return (
            <tr key={ index }>
              <td>{ droplet.id }</td>
              <td>
                <a href={breweryDetail}>
              { droplet.name }</a>
              </td>
              <td>{ droplet.brewery_type}</td>
              <td>{ droplet.street } ,{ droplet.city } ,{ droplet.state } { droplet.postal_code }</td>
              <td><a href={droplet.website_url}>{ droplet.website_url }</a></td>
            </tr>
          )
         }) : <tr><td colSpan="5">Loading...</td></tr> }
      </tbody>
    </table>
  );
}

export default Table
