import React from 'react';

class FetchBrewery extends React.Component (breweryId){
    state = {
        loading: true
    };

    async componentDidMount() {
        const url = "https://api.openbrewerydb.org/breweries/"+breweryId;
        const response = await fetch(url);
        const data = await response.json();
        this.setState({ brewery: data, loading: false });
    }

    renderData() {
        return this.state.brewery.map((brewery, index) => {
            const { id, name, brewery_type, street, city, state, postal_code, website_url } = brewery; //destructuring
            return (
                <tr key={id}>
                    <td>{name}</td>
                    <td>{brewery_type}</td>
                    <td>{street} {city} {state} {postal_code}</td>
                    <td>{website_url}</td>
                </tr>
            )

        })
    }


    render() {
        if (this.state.loading) {
            return <div>loading</div>
        }

        if (!this.state.brewery) {
            return <div>No data</div>
        }
        return (
            <div>
                Brewery Detail
            </div>
        );
    }
}

export default FetchBrewery
