import React from 'react';
import BreweryDetail from './BreweryDetail';

class Brewery extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            trExpand: false
        };
        this.toggle = this.toggle.bind(this);

    }

    toggle() {
        this.setState({trExpand: !this.state.trExpand})
    }

    render() {
        let breweryId = this.props.breweryProps["brewery"]["id"];

        return (
            <div>
                <table className="table table-hover" >
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Name</th>
                            <th>Type</th>
                            <th>Address</th>
                            <th>Website URL</th>
                        </tr>
                    </thead>
                    <tbody>
                        {(this.props.breweries.length > 0) ? this.props.breweries.map((brewery, index) => {
                            let breweryDetail = "https://api.openbrewerydb.org/breweries/" + brewery.id;

                            return (
                                this.state.trExpand ? ( 
                                    <tr>
                                        <td>
                                            <BreweryDetail breweryId={brewery.id}/>
                                        </td>
                                    </tr>
                                ):
                                (
                                <tr  onClick = {this.toggle}>
                                    <td>{brewery.id}</td>
                                    <td>
                                        <a href={breweryDetail}>{brewery.name}</a>
                                    </td>
                                    <td>{brewery.brewery_type}</td>
                                    <td>{brewery.street}, {brewery.city}, {brewery.state} {brewery.postal_code}</td>
                                    <td><a href={brewery.website_url}>{brewery.website_url}</a></td>

                                </tr>)
                            )
                        }) : <tr><td colSpan="5">Loading Breweries...</td></tr>}
                    </tbody>
                </table>
            </div>
        );

    }
}

export default Brewery;
