import React from 'react';
import FetchBrewery from './FetchBrewery'

class Table extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          breweries: []
        }
        this.searchByCity = this.searchByCity.bind(this);
      }
      async componentDidMount() {
        const url = "https://api.openbrewerydb.org/breweries/";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({ breweries: data, loading: false });
      }

      async searchByCity(e) {
        let SearchKeyword= e.target.value;
        if(e.target.value !== "") {
          const url = "https://api.openbrewerydb.org/breweries?by_city="+ SearchKeyword;
          const response = await fetch(url);
          const data = await response.json();
          this.setState({ breweries: data, loading: false });
        }

      }

    render() {
        return (
            <div>
          <table className="table table-hover" >
            <thead>
              <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Type</th>
                <th>Address</th>
                <th>Website URL</th>
              </tr>
            </thead>
            <tbody>
            { (breweries.length > 0) ? breweries.map( (brewery, index) => {
                let breweryDetail = "https://api.openbrewerydb.org/breweries/"+brewery.id;
                 return (
                  <tr key={ index } >
                    <td>{ brewery.id }</td>
                    <td>
                      <a href={breweryDetail}>{ brewery.name }</a>
                    </td>
                    <td>{ brewery.brewery_type}</td>
                    <td>{ brewery.street }, { brewery.city }, { brewery.state } { brewery.postal_code }</td>
                    <td><a href={brewery.website_url}>{ brewery.website_url }</a></td>
                  </tr>
                )
               }) : <tr><td colSpan="5">Loading Breweries...</td></tr> }
            </tbody>
          </table></div>
        );

    }
}

export default Table;
