import React from 'react';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';

const mapStyles = {
    width: '100%',
    height: '80%',
  };
export class BreweryMap extends React.Component{
    constructor(props) {
        super(props);
        this.center = {
            lat: 59.95,
            lng: 30.33
          };
        this.zoom= 11;
        this.state = {
            showingInfoWindow: false,  //Hides or the shows the infoWindow
            activeMarker: {},          //Shows the active marker upon click
            selectedPlace: {}          //Shows the infoWindow to the selected place upon a marker
          };
    }

    onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };


    render() {

        let breweryLat= this.props.brewery["latitude"];
        let breweryLng= this.props.brewery["longitude"];

        return(
            <Map
            google={this.props.google}
            zoom={8}
            style={mapStyles}
            initialCenter={{ lat: 47.444, lng: -122.176}}
            >
            <Marker 
            marker={this.state.activeMarker} 
            position={{ lat: breweryLat, lng:breweryLng }}

          onClick={this.onMarkerClick}
          name={'Kenyatta International Convention Centre'}
           />
            </Map>
        );
    }
}
//export default BreweryMap;

export default GoogleApiWrapper({
    apiKey: 'AIzaSyA-7d1-o3I4_FPxDYGOM6OAmON9zPdaZ-Q '
  })(BreweryMap);
